import { test, expect } from '@playwright/test';

test('Successful login with valid credentials', async ({ page }) => {
  const credentials = {
    email: 'user@example.com',
    password: 'password123',
  };

  await test.step('Navigate to the login page and enter valid credentials', async () => {
    await page.goto('/login');
    
    // Fill in the email and password fields using accessible locators
    await page.getByLabel('Email').fill(credentials.email);
    await page.getByLabel('Password').fill(credentials.password);
    
    // Click the login button
    await page.getByRole('button', { name: 'Log in' }).click();
  });

  await test.step('Verify redirection to the personal dashboard', async () => {
    // Verify the URL contains 'dashboard' and a dashboard-specific element is visible
    await expect(page).toHaveURL(/.*dashboard/);
    await expect(page.getByRole('heading', { name: 'Dashboard' })).toBeVisible();
  });
});
