import { test, expect } from '@playwright/test';

test('Failed Login - Incorrect Password', async ({ page }) => {
  // Navigate to the login page
  await page.goto('/login');

  // Step: Enter valid email and incorrect password
  const emailInput = page.getByLabel(/email/i);
  const passwordInput = page.getByLabel(/password/i);

  await emailInput.fill('valid-user@example.com');
  await passwordInput.fill('incorrectPassword123');

  // Expected: Input fields are populated
  await expect(emailInput).toHaveValue('valid-user@example.com');
  await expect(passwordInput).toHaveValue('incorrectPassword123');

  // Step: Click the login button
  await page.getByRole('button', { name: /log in|submit/i }).click();

  // Expected: Error message 'Invalid credentials' is displayed
  const errorMessage = page.getByText('Invalid credentials');
  await expect(errorMessage).toBeVisible();

  // Expected: User remains on the login page (assuming URL contains /login)
  await expect(page).toHaveURL(/.*login/);
});
