import { test, expect } from '@playwright/test';

test('Failed login with incorrect password', async ({ page }) => {
  // Navigate to the login page
  await page.goto('/login');

  // Enter a valid email and an incorrect password, then click login.
  // Using user-facing locators (Labels and Roles) for better resilience.
  await page.getByLabel(/email/i).fill('valid-user@example.com');
  await page.getByLabel(/password/i).fill('wrongpassword123');
  await page.getByRole('button', { name: /log in|login/i }).click();

  // Expect: Authentication error message is displayed.
  // getByRole('alert') is the standard for error messages/notifications.
  const errorMessage = page.getByRole('alert');
  await expect(errorMessage).toBeVisible();
  
  // Verify the user is still on the login page (URL contains login)
  await expect(page).toHaveURL(/.*login/);
});
