import { test, expect } from '@playwright/test';

test('Login Failure with Incorrect Password', async ({ page }) => {
  // Navigate to the login page
  await page.goto('/login');

  // Enter valid email and an incorrect password
  // Using getByLabel for better accessibility and robustness
  await page.getByLabel('Email').fill('valid-user@example.com');
  await page.getByLabel('Password').fill('incorrectPassword123');

  // Click the login button
  await page.getByRole('button', { name: /log in|submit/i }).click();

  // Expect: Error message 'Invalid credentials' is displayed
  const errorMessage = page.getByText('Invalid credentials');
  await expect(errorMessage).toBeVisible();

  // Verify that the user remains on the login page (URL check)
  await expect(page).toHaveURL(/.*login/);
});
