import { test, expect } from '@playwright/test';

test('Automatic Redirection for Unauthenticated Users', async ({ page }) => {
  // Step: Navigate directly to '/dashboard'
  await page.goto('/dashboard');

  // Expect: User is redirected to the login page
  // We verify the URL contains 'login' as part of the redirection logic
  await expect(page).toHaveURL(/.*login.*/);

  // Expect: the login form is visible
  // Assuming a standard form element exists on the login page
  const loginForm = page.locator('form');
  await expect(loginForm).toBeVisible();
});
