import { test, expect } from '@playwright/test';

test('Login attempt with empty email field', async ({ page }) => {
  // Leave the email field empty
  await page.locator('input[name="email"]').fill('');

  // Enter any password
  await page.locator('input[name="password"]').fill('SecurePassword123!');

  // Click login
  await page.locator('button[type="submit"]').click();

  // Expect: Error message 'Email is required' is displayed.
  const errorMessage = page.locator('text=Email is required');
  await expect(errorMessage).toBeVisible();
});
