import { test, expect } from '@playwright/test';

test('Login Failure with Invalid Email Format', async ({ page }) => {
  // Navigate to the login page
  await page.goto('/login');

  // Enter an improperly formatted email string
  // Using getByLabel ensures the field is accessible and correctly associated
  await page.getByLabel('Email').fill('not-a-valid-email');

  // Click the login button
  // Using getByRole is more robust than generic CSS selectors
  await page.getByRole('button', { name: /log in|submit/i }).click();

  // Verify validation message appears
  const errorMessage = page.getByText('Please enter a valid email address');
  await expect(errorMessage).toBeVisible();
});
