import { test, expect } from '@playwright/test';

test('Unauthorized Access Redirection', async ({ page }) => {
  // Attempt to navigate directly to the protected account settings page
  await page.goto('/account/settings');

  // Verify the user is redirected to the login page
  // We check that the URL path contains 'login'
  await expect(page).toHaveURL(/\/login/);

  // Verify that a message indicating login is required is visible
  // Using getByText with a case-insensitive regex to handle common variations
  const loginMessage = page.getByText(/login required|please log in|sign in/i);
  await expect(loginMessage).toBeVisible();
});
