import { test, expect } from '@playwright/test';

test('Validation of Empty Mandatory Fields', async ({ page }) => {
  // Navigate to the login page (assuming base URL is set in config)
  await page.goto('/login');

  // Step: Leave email and password empty and click login
  // We ensure the fields are empty first, then click the submit button
  await page.locator('input[name="email"]').fill('');
  await page.locator('input[name="password"]').fill('');
  await page.locator('button[type="submit"]').click();

  // Expect: Validation errors are displayed for both the email and password fields
  const emailError = page.locator('#email-error-message');
  const passwordError = page.locator('#password-error-message');

  await expect(emailError).toBeVisible();
  await expect(emailError).not.toBeEmpty();
  
  await expect(passwordError).toBeVisible();
  await expect(passwordError).not.toBeEmpty();
});
