import { test, expect } from '@playwright/test';

test('Verification of login UI components', async ({ page }) => {
  // Assume navigation to the login page
  await page.goto('/');

  // Step: Observe the login page elements.
  // Expect: Email field, password field, Submit button, and Forgot Password link are visible.

  // Verify Email field visibility
  const emailField = page.locator('input[type="email"], [name="email"], [placeholder*="email" i]');
  await expect(emailField).toBeVisible();

  // Verify Password field visibility
  const passwordField = page.locator('input[type="password"], [name="password"], [placeholder*="password" i]');
  await expect(passwordField).toBeVisible();

  // Verify Submit button visibility
  const submitButton = page.locator('button[type="submit"], button:has-text("Login"), button:has-text("Submit")');
  await expect(submitButton).toBeVisible();

  // Verify Forgot Password link visibility
  const forgotPasswordLink = page.locator('a:has-text("Forgot Password")');
  await expect(forgotPasswordLink).toBeVisible();
});
