import { test, expect } from '@playwright/test';

test('Login Interface Element Verification', async ({ page }) => {
  // Navigate to the login page
  await page.goto('/');

  // Verify Identity field is visible (checking for common labels or placeholders)
  const identityField = page.getByLabel(/email|username|identity/i).or(page.getByPlaceholder(/email|username|identity/i));
  await expect(identityField.first()).toBeVisible();

  // Verify Password field is visible
  const passwordField = page.getByLabel(/password/i).or(page.getByPlaceholder(/password/i));
  await expect(passwordField.first()).toBeVisible();

  // Verify 'Forgot Password' link is visible
  const forgotPasswordLink = page.getByRole('link', { name: /forgot password/i });
  await expect(forgotPasswordLink).toBeVisible();

  // Verify 'Sign Up' link is visible
  const signUpLink = page.getByRole('link', { name: /sign up/i });
  await expect(signUpLink).toBeVisible();
});
