import { test, expect } from '@playwright/test';

test('Mandatory Field Validation - Verify that the login form cannot be submitted with empty fields', async ({ page }) => {
  // Navigate to the login page (assuming /login endpoint)
  await page.goto('/login');

  // Step: Ensure email and password fields are blank
  // Using getByLabel for better accessibility and reliability
  await page.getByLabel(/email/i).fill('');
  await page.getByLabel(/password/i).fill('');

  // Click the login/submit button
  await page.getByRole('button', { name: /log in|login|submit/i }).click();

  // Define locators for error messages
  // Prioritize data-testid for robustness in automated testing
  const emailError = page.locator('[data-testid="email-error"], #email-error');
  const passwordError = page.locator('[data-testid="password-error"], #password-error');

  // Assertions to verify validation messages are displayed and contain relevant text
  await expect(emailError).toBeVisible();
  await expect(emailError).toContainText(/required|empty|enter/i);

  await expect(passwordError).toBeVisible();
  await expect(passwordError).toContainText(/required|empty|enter/i);
});
