import { test, expect } from '@playwright/test';

test('Redirection to intended page after login', async ({ page }) => {
  // Attempt to access the 'Settings' page directly. (Expect: User is redirected to the login page.)
  await page.goto('/settings');
  await expect(page).toHaveURL(/.*login.*/);

  // Enter valid credentials and click login.
  // Assuming standard selectors for username, password and login button
  await page.fill('input[name="username"]', 'testuser');
  await page.fill('input[name="password"]', 'password123');
  await page.click('button[type="submit"]');

  // (Expect: User is redirected to the 'Settings' page.)
  await expect(page).toHaveURL(/.*settings.*/);
  await expect(page.locator('h1')).toContainText('Settings');
});
