import { test, expect } from '@playwright/test';

test('Verification of Login UI Elements', async ({ page }) => {
  // Observe the loaded page (Expect: Two input fields, 'Forgot Password' link, and security elements are visible)

  // Verify that two input fields (typically username/email and password) are visible
  const inputFields = page.locator('input:not([type="submit"]):not([type="button"])');
  await expect(inputFields).toHaveCount(2);
  await expect(inputFields.nth(0)).toBeVisible();
  await expect(inputFields.nth(1)).toBeVisible();

  // Verify 'Forgot Password' link is visible
  const forgotPasswordLink = page.getByRole('link', { name: /forgot password/i });
  await expect(forgotPasswordLink).toBeVisible();

  // Verify security elements are visible (e.g., security badges, icons, or text)
  // Using a generic locator for common security indicators
  const securityElement = page.locator('text=/secure|security|protected/i').first();
  await expect(securityElement).toBeVisible();
});
